/**
 * 参加ルーム選択オプションClick
 * @param   element option
 * @return  void
 * @since   v 3.0.0.0
 */
		clickGroupOption: function(option) {
			option = $(option);
			var select = option.parent();
			var parent_id = option.attr('data-parent-id');
			if(option.prop('disabled')) {
				return;
			}
			var selects = null;
			var keyArr = option.val().split("_");
			var roomId = keyArr[keyArr.length - 1];

			if(!select.hasClass('user-selectlist-enroll')) {
				// 全ルーム一覧
				if(!option.prop('selected')) {
					selects = select.children('[data-parent-id='+roomId+']').prop('selected', false);
				} else {
					// 子グループ　親ルームも選択
					if(parent_id) {
						selects = select.children('[data-room-id='+parent_id+']:first').prop('selected', true);
					}
				}

			} else {
				// 参加させるルーム
				if(!option.prop('selected')) {
					if(parent_id) {
						selects = select.children('[data-room-id='+parent_id+']:first').prop('selected', false);
					}
				} else {
					// 親ルーム　子グループも選択
					selects = select.children('[data-parent-id='+roomId+']').prop('selected', true);
				}
			}
			if(selects) {
				selects.each(function(){
					$.User.clickGroupOption(this);
				});
			}
		},

